import { useEffect } from 'react';

export const useScrollAnimation = () => {
  useEffect(() => {
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver(function(entries) {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.style.opacity = 1;
          entry.target.style.transform = 'translateY(0)';
        }
      });
    }, observerOptions);

    // Observe all elements for animation
    const animatedElements = document.querySelectorAll(
      '.card, .feature-card, .testimonial-card, .cta-content, .visual-item, ' +
      '.section-title, .section-subtitle, .tool-card, .category-card, .feature-item, ' +
      '.search-container, .section-header, .agent-card, .workflow-step, ' +
      '.benefit-card, .filter-group, .categories-grid, .tools-grid, ' +
      '.agents-grid, .features-grid, .testimonials-grid, .floating-cards, ' +
      '.hero-content, .showcase-content, .how-it-works, .benefits-section, ' +
      '.filters-section, .tool-features, .capabilities-list, .agent-actions, ' +
      '.tool-actions, .cta-buttons, .nav-links, .nav-buttons, .logo, ' +
      '.banner-content, .footer-content, .social-links, .footer-column, ' +
      '.visual-container, .workflow-steps, .filter-options, .step-icon, ' +
      '.benefit-icon, .agent-icon, .tool-icon, .category-icon, .feature-icon'
    );
    
    animatedElements.forEach(el => {
      el.style.opacity = 0;
      el.style.transform = 'translateY(20px)';
      el.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
      observer.observe(el);
    });

    // Add staggered animation delays for grid items
    const gridItems = document.querySelectorAll('.tool-card, .agent-card, .feature-card, .benefit-card, .workflow-step');
    gridItems.forEach((item, index) => {
      item.style.transitionDelay = `${(index % 6) * 0.1}s`;
    });

    // Add hover effect to visual items
    const visualItems = document.querySelectorAll('.visual-item');
    visualItems.forEach(item => {
      item.addEventListener('mouseenter', function() {
        this.style.transform = 'scale(1.05) translateY(-5px)';
        this.style.zIndex = '10';
      });
      
      item.addEventListener('mouseleave', function() {
        this.style.transform = '';
        this.style.zIndex = '';
      });
    });

    // Add hover effects to cards
    const cards = document.querySelectorAll('.card, .tool-card, .agent-card, .feature-card, .benefit-card');
    cards.forEach(card => {
      card.addEventListener('mouseenter', function() {
        this.style.transform = 'translateY(-10px) scale(1.02)';
      });
      
      card.addEventListener('mouseleave', function() {
        this.style.transform = 'translateY(0) scale(1)';
      });
    });

    // Add click animation to buttons
    const buttons = document.querySelectorAll('.btn');
    buttons.forEach(button => {
      button.addEventListener('click', function() {
        this.style.transform = 'scale(0.95)';
        setTimeout(() => {
          this.style.transform = '';
        }, 150);
      });
    });

    // Add loading animation for images (if any)
    const images = document.querySelectorAll('img');
    images.forEach(img => {
      img.style.opacity = '0';
      img.style.transition = 'opacity 0.5s ease';
      
      img.onload = () => {
        img.style.opacity = '1';
      };
      
      // If image is already loaded
      if (img.complete) {
        img.style.opacity = '1';
      }
    });

    // Smooth scroll for anchor links
    const anchorLinks = document.querySelectorAll('a[href^="#"]');
    anchorLinks.forEach(link => {
      link.addEventListener('click', function(e) {
        e.preventDefault();
        const targetId = this.getAttribute('href').substring(1);
        const targetElement = document.getElementById(targetId);
        
        if (targetElement) {
          targetElement.scrollIntoView({
            behavior: 'smooth',
            block: 'start'
          });
        }
      });
    });

    // Parallax effect for background elements
    const handleParallax = () => {
      const scrolled = window.pageYOffset;
      const parallaxElements = document.querySelectorAll('.parallax');
      
      parallaxElements.forEach(element => {
        const speed = element.dataset.speed || 0.5;
        element.style.transform = `translateY(${scrolled * speed}px)`;
      });
    };

    // Add parallax to specific elements if they exist
    const heroBackground = document.querySelector('.hero');
    if (heroBackground) {
      heroBackground.classList.add('parallax');
      heroBackground.dataset.speed = '0.3';
    }

    window.addEventListener('scroll', handleParallax);

    // Cleanup function
    return () => {
      observer.disconnect();
      
      // Clean up event listeners
      visualItems.forEach(item => {
        item.removeEventListener('mouseenter', () => {});
        item.removeEventListener('mouseleave', () => {});
      });
      
      cards.forEach(card => {
        card.removeEventListener('mouseenter', () => {});
        card.removeEventListener('mouseleave', () => {});
      });
      
      buttons.forEach(button => {
        button.removeEventListener('click', () => {});
      });
      
      anchorLinks.forEach(link => {
        link.removeEventListener('click', () => {});
      });
      
      window.removeEventListener('scroll', handleParallax);
    };
  }, []);
};

// Additional utility function for manual animation triggering
export const animateElement = (element, animationType = 'fadeIn') => {
  const animations = {
    fadeIn: {
      opacity: 1,
      transform: 'translateY(0)'
    },
    slideInLeft: {
      opacity: 1,
      transform: 'translateX(0)'
    },
    slideInRight: {
      opacity: 1,
      transform: 'translateX(0)'
    },
    scaleIn: {
      opacity: 1,
      transform: 'scale(1)'
    }
  };

  if (element && animations[animationType]) {
    element.style.transition = 'all 0.6s ease';
    Object.assign(element.style, animations[animationType]);
  }
};

// Function to check if element is in viewport
export const isInViewport = (element) => {
  const rect = element.getBoundingClientRect();
  return (
    rect.top >= 0 &&
    rect.left >= 0 &&
    rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
    rect.right <= (window.innerWidth || document.documentElement.clientWidth)
  );
};

// Function to add scroll-triggered animations to specific elements
export const useScrollTriggeredAnimations = () => {
  useEffect(() => {
    const handleScroll = () => {
      const elements = document.querySelectorAll('.scroll-trigger');
      
      elements.forEach(element => {
        if (isInViewport(element)) {
          const animationType = element.dataset.animation || 'fadeIn';
          animateElement(element, animationType);
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    // Initial check
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
};
