import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const handleSignIn = () => {
    navigate('/signin');
    setIsMobileMenuOpen(false);
  };

  const handleGetStarted = () => {
    navigate('/get-started');
    setIsMobileMenuOpen(false);
  };

  const handleLogoClick = () => {
    navigate('/');
    setIsMobileMenuOpen(false);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const isActiveRoute = (path) => {
    return location.pathname === path;
  };

  return (
    <header>
      <div className="container">
        <nav className="navbar">
          <div className="logo" onClick={handleLogoClick} style={{cursor: 'pointer'}}>
            <i className="fas fa-robot"></i>
            <span>Nexus AI</span>
          </div>
          
          <div className={`nav-links ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
            <Link 
              to="/" 
              className={isActiveRoute('/') ? 'active' : ''}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              Home
            </Link>
            <Link 
              to="/ai-tools" 
              className={isActiveRoute('/ai-tools') ? 'active' : ''}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              AI Tools
            </Link>
            <Link 
              to="/ai-agents" 
              className={isActiveRoute('/ai-agents') ? 'active' : ''}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              AI Agents
            </Link>
            <Link 
              to="/automation" 
              className={isActiveRoute('/automation') ? 'active' : ''}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              Automation
            </Link>
            <Link 
              to="/pricing" 
              className={isActiveRoute('/pricing') ? 'active' : ''}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              Pricing
            </Link>
            <Link 
              to="/blog" 
              className={isActiveRoute('/blog') ? 'active' : ''}
              onClick={() => setIsMobileMenuOpen(false)}
            >
              Blog
            </Link>
          </div>
          
          <div className="nav-buttons">
            <button className="btn btn-outline" onClick={handleSignIn}>
              Sign In
            </button>
            <button className="btn btn-primary" onClick={handleGetStarted}>
              Get Started
            </button>
            
            {/* Mobile Menu Button */}
            <button 
              className="mobile-menu-btn"
              onClick={toggleMobileMenu}
            >
              <i className={`fas ${isMobileMenuOpen ? 'fa-times' : 'fa-bars'}`}></i>
            </button>
          </div>
        </nav>
      </div>
    </header>
  );
};

export default Header;
