import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';

const CaseStudies = () => {
  const [activeIndustry, setActiveIndustry] = useState('all');
  const [activeSolution, setActiveSolution] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  const industries = [
    { id: 'all', name: 'All Industries', count: 12 },
    { id: 'technology', name: 'Technology', count: 4 },
    { id: 'healthcare', name: 'Healthcare', count: 2 },
    { id: 'finance', name: 'Finance', count: 3 },
    { id: 'ecommerce', name: 'E-commerce', count: 2 },
    { id: 'education', name: 'Education', count: 1 }
  ];

  const solutions = [
    { id: 'all', name: 'All Solutions' },
    { id: 'ai-chatbots', name: 'AI Chatbots' },
    { id: 'automation', name: 'Workflow Automation' },
    { id: 'ai-agents', name: 'AI Agents' },
    { id: 'analytics', name: 'AI Analytics' }
  ];

  const caseStudies = [
    {
      id: 1,
      title: 'TechSolutions Inc: 85% Efficiency Boost with AI Automation',
      company: 'TechSolutions Inc',
      industry: 'technology',
      solutions: ['automation', 'ai-agents'],
      excerpt: 'How a leading tech company automated their customer support and internal processes, saving 200+ hours monthly.',
      results: [
        { metric: '85%', description: 'Increase in operational efficiency' },
        { metric: '200+', description: 'Hours saved per month' },
        { metric: '40%', description: 'Reduction in response time' }
      ],
      duration: '3 months',
      image: '/api/placeholder/400/250',
      featured: true,
      logo: 'TS'
    },
    {
      id: 2,
      title: 'MediCare Plus: Transforming Patient Care with AI Chatbots',
      company: 'MediCare Plus',
      industry: 'healthcare',
      solutions: ['ai-chatbots'],
      excerpt: 'Healthcare provider implements AI chatbots to handle patient inquiries and appointment scheduling.',
      results: [
        { metric: '24/7', description: 'Patient support availability' },
        { metric: '65%', description: 'Reduction in call center volume' },
        { metric: '95%', description: 'Patient satisfaction rate' }
      ],
      duration: '2 months',
      image: '/api/placeholder/400/250',
      featured: true,
      logo: 'MP'
    },
    {
      id: 3,
      title: 'Global Bank: AI-Powered Fraud Detection System',
      company: 'Global Bank',
      industry: 'finance',
      solutions: ['ai-agents', 'analytics'],
      excerpt: 'Implementing advanced AI agents for real-time fraud detection and prevention in financial transactions.',
      results: [
        { metric: '99.8%', description: 'Detection accuracy' },
        { metric: '60%', description: 'Faster threat identification' },
        { metric: '$2.3M', description: 'Potential fraud prevented' }
      ],
      duration: '6 months',
      image: '/api/placeholder/400/250',
      featured: true,
      logo: 'GB'
    },
    {
      id: 4,
      title: 'ShopEasy: Personalized E-commerce with AI Recommendations',
      company: 'ShopEasy',
      industry: 'ecommerce',
      solutions: ['ai-chatbots', 'analytics'],
      excerpt: 'E-commerce platform boosts sales with AI-powered product recommendations and customer engagement.',
      results: [
        { metric: '35%', description: 'Increase in conversion rate' },
        { metric: '25%', description: 'Higher average order value' },
        { metric: '50%', description: 'More customer engagement' }
      ],
      duration: '4 months',
      image: '/api/placeholder/400/250',
      logo: 'SE'
    },
    {
      id: 5,
      title: 'EduTech Solutions: AI Tutoring and Learning Assistant',
      company: 'EduTech Solutions',
      industry: 'education',
      solutions: ['ai-chatbots', 'ai-agents'],
      excerpt: 'Educational platform enhances learning outcomes with personalized AI tutoring and progress tracking.',
      results: [
        { metric: '45%', description: 'Improvement in test scores' },
        { metric: '80%', description: 'Student engagement rate' },
        { metric: '3x', description: 'Faster learning progress' }
      ],
      duration: '5 months',
      image: '/api/placeholder/400/250',
      logo: 'ES'
    },
    {
      id: 6,
      title: 'FinTech Innovators: Automated Investment Advisory',
      company: 'FinTech Innovators',
      industry: 'finance',
      solutions: ['ai-agents', 'automation'],
      excerpt: 'Robo-advisory platform uses AI agents to provide personalized investment strategies and portfolio management.',
      results: [
        { metric: '90%', description: 'Process automation' },
        { metric: '18%', description: 'Higher returns for clients' },
        { metric: '10x', description: 'More clients served' }
      ],
      duration: '4 months',
      image: '/api/placeholder/400/250',
      logo: 'FI'
    },
    {
      id: 7,
      title: 'HealthTech Labs: Medical Research Acceleration',
      company: 'HealthTech Labs',
      industry: 'healthcare',
      solutions: ['ai-agents', 'analytics'],
      excerpt: 'Research institution accelerates medical discoveries with AI-powered data analysis and pattern recognition.',
      results: [
        { metric: '70%', description: 'Faster data analysis' },
        { metric: '3x', description: 'More research papers' },
        { metric: '60%', description: 'Cost reduction in research' }
      ],
      duration: '8 months',
      image: '/api/placeholder/400/250',
      logo: 'HL'
    },
    {
      id: 8,
      title: 'CloudTech Systems: IT Infrastructure Automation',
      company: 'CloudTech Systems',
      industry: 'technology',
      solutions: ['automation'],
      excerpt: 'Cloud service provider automates infrastructure management and monitoring with intelligent AI systems.',
      results: [
        { metric: '95%', description: 'Automated monitoring' },
        { metric: '50%', description: 'Reduced downtime' },
        { metric: '40%', description: 'Lower operational costs' }
      ],
      duration: '3 months',
      image: '/api/placeholder/400/250',
      logo: 'CS'
    }
  ];

  const stats = [
    { number: '85%', label: 'Average Efficiency Gain' },
    { number: '200+', label: 'Hours Saved Weekly' },
    { number: '95%', label: 'Client Satisfaction' },
    { number: '50%', label: 'Cost Reduction' }
  ];

  const filteredCaseStudies = caseStudies.filter(study => {
    const matchesIndustry = activeIndustry === 'all' || study.industry === activeIndustry;
    const matchesSolution = activeSolution === 'all' || study.solutions.includes(activeSolution);
    const matchesSearch = study.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         study.company.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         study.excerpt.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesIndustry && matchesSolution && matchesSearch;
  });

  const featuredCaseStudies = caseStudies.filter(study => study.featured);

  const getIndustryColor = (industry) => {
    const colors = {
      technology: 'var(--primary)',
      healthcare: '#4CAF50',
      finance: '#FF9800',
      ecommerce: '#9C27B0',
      education: '#2196F3'
    };
    return colors[industry] || 'var(--primary)';
  };

  const getSolutionColor = (solution) => {
    const colors = {
      'ai-chatbots': '#4CAF50',
      'automation': '#2196F3',
      'ai-agents': '#9C27B0',
      'analytics': '#FF9800'
    };
    return colors[solution] || 'var(--primary)';
  };

  return (
    <div className="case-studies-page">
      {/* Banner */}
      <div className="banner">
        <div className="container">
          <div className="banner-content">
            <div className="banner-text">
              <span className="banner-highlight">NEW:</span> Introducing Nexus AI Pro with advanced multi-agent workflows. 
              <span className="banner-highlight"> Try it free for 14 days!</span>
            </div>
          </div>
          <div className="banner-decoration"></div>
        </div>
      </div>

      <Header />
      
      {/* Hero Section */}
      <section className="case-studies-hero">
        <div className="container">
          <div className="hero-content">
            <h1>Success Stories</h1>
            <p>Discover how businesses across industries are transforming their operations and achieving remarkable results with Nexus AI.</p>
            
            <div className="search-container">
              <i className="fas fa-search"></i>
              <input
                type="text"
                placeholder="Search case studies..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="case-studies-stats">
        <div className="container">
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <div key={index} className="stat-item">
                <div className="stat-number">{stat.number}</div>
                <div className="stat-label">{stat.label}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Case Studies */}
      {featuredCaseStudies.length > 0 && (
        <section className="featured-case-studies">
          <div className="container">
            <h2 className="section-title">Featured Success Stories</h2>
            <p className="section-subtitle">In-depth looks at transformative AI implementations</p>
            
            <div className="featured-grid">
              {featuredCaseStudies.map(study => (
                <div key={study.id} className="featured-case-study">
                  <div className="case-study-image">
                    <div 
                      className="image-placeholder"
                      style={{ 
                        background: `linear-gradient(135deg, ${getIndustryColor(study.industry)}20, var(--primary)20)`
                      }}
                    >
                      <div className="company-logo">
                        {study.logo}
                      </div>
                    </div>
                    <div className="case-study-badges">
                      <span 
                        className="badge industry"
                        style={{ backgroundColor: getIndustryColor(study.industry) }}
                      >
                        {industries.find(ind => ind.id === study.industry)?.name}
                      </span>
                    </div>
                  </div>
                  <div className="case-study-content">
                    <h3>{study.title}</h3>
                    <div className="company-info">
                      <span className="company-name">{study.company}</span>
                      <span className="duration">{study.duration} implementation</span>
                    </div>
                    <p>{study.excerpt}</p>
                    
                    <div className="results-grid">
                      {study.results.map((result, index) => (
                        <div key={index} className="result-item">
                          <div className="result-metric">{result.metric}</div>
                          <div className="result-description">{result.description}</div>
                        </div>
                      ))}
                    </div>

                    <div className="solutions-tags">
                      {study.solutions.map(solution => (
                        <span 
                          key={solution}
                          className="solution-tag"
                          style={{ 
                            backgroundColor: getSolutionColor(solution),
                            color: 'white'
                          }}
                        >
                          {solutions.find(sol => sol.id === solution)?.name}
                        </span>
                      ))}
                    </div>

                    <Link to={`/case-studies/${study.id}`} className="btn btn-primary">
                      Read Full Case Study
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>
      )}

      {/* All Case Studies */}
      <section className="all-case-studies">
        <div className="container">
          <div className="case-studies-header">
            <h2>All Case Studies</h2>
            <div className="filters">
              <div className="filter-group">
                <label>Industry:</label>
                <select 
                  value={activeIndustry} 
                  onChange={(e) => setActiveIndustry(e.target.value)}
                  className="filter-select"
                >
                  {industries.map(industry => (
                    <option key={industry.id} value={industry.id}>
                      {industry.name} ({industry.count})
                    </option>
                  ))}
                </select>
              </div>
              <div className="filter-group">
                <label>Solution:</label>
                <select 
                  value={activeSolution} 
                  onChange={(e) => setActiveSolution(e.target.value)}
                  className="filter-select"
                >
                  {solutions.map(solution => (
                    <option key={solution.id} value={solution.id}>
                      {solution.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          <div className="case-studies-grid">
            {filteredCaseStudies.map(study => (
              <div key={study.id} className="case-study-card">
                <div className="case-study-image">
                  <div 
                    className="image-placeholder"
                    style={{ 
                      background: `linear-gradient(135deg, ${getIndustryColor(study.industry)}20, var(--primary)20)`
                    }}
                  >
                    <div className="company-logo">
                      {study.logo}
                    </div>
                  </div>
                  <div className="case-study-badges">
                    <span 
                      className="badge industry"
                      style={{ backgroundColor: getIndustryColor(study.industry) }}
                    >
                      {industries.find(ind => ind.id === study.industry)?.name}
                    </span>
                  </div>
                </div>
                <div className="case-study-content">
                  <h3>{study.title}</h3>
                  <div className="company-info">
                    <span className="company-name">{study.company}</span>
                    <span className="duration">{study.duration}</span>
                  </div>
                  <p>{study.excerpt}</p>
                  
                  <div className="results-preview">
                    {study.results.slice(0, 2).map((result, index) => (
                      <div key={index} className="result-preview">
                        <span className="result-metric">{result.metric}</span>
                        <span className="result-label">{result.description}</span>
                      </div>
                    ))}
                  </div>

                  <div className="case-study-actions">
                    <Link to={`/case-studies/${study.id}`} className="read-more">
                      Read Case Study <i className="fas fa-arrow-right"></i>
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {filteredCaseStudies.length === 0 && (
            <div className="no-case-studies">
              <i className="fas fa-search"></i>
              <h3>No case studies found</h3>
              <p>Try adjusting your search or filters</p>
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="case-studies-cta">
        <div className="container">
          <div className="cta-content">
            <h2>Ready to Write Your Success Story?</h2>
            <p>Join industry leaders who are transforming their businesses with AI-powered solutions.</p>
            <div className="cta-buttons">
              <Link to="/get-started" className="btn btn-primary btn-large">
                Start Free Trial
              </Link>
              <Link to="/contact" className="btn btn-outline btn-large">
                Schedule Demo
              </Link>
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default CaseStudies;
